/**
 * Maneja el componente Contador
 */
class Contador {

    constructor(opciones) {
        this.contenedor = null;
        this.min = 0;
        this.max = 100;
        this.fuente = null;
        this.url = null;

        if (typeof opciones === 'object') {
            //contenedor
            switch (typeof opciones['contenedor']) {
                case 'string':
                    this.contenedor = $('#' + opciones['contenedor']);
                    break;

                case 'object':
                    this.contenedor = opciones['contenedor'];
                    break;
            }

            //min
            if (typeof opciones['min'] !== 'undefined') {
                this.min = parseFloat(opciones['min']) || 0;
            }

            //max
            if (typeof opciones['max'] !== 'undefined') {
                this.max = parseFloat(opciones['max']) || 0;
            }

            //fuente
            if (typeof opciones['fuente'] === 'string' && opciones['fuente'].length) {
                this.fuente = opciones['fuente'];
            }
            
            //url
            if (typeof opciones['url'] === 'string' && opciones['url'].length) {
                this.url = opciones['url'];
            }
            
        }

        if (this.url === null && typeof url_contar_total === 'string') {
            this.url = url_contar_total;
        }

        if (this.fuente !== null) {
            this.cargarTotal();
        }
        else {
            this.contar();
        }
    }


    contar(val) {
        if (this.contenedor === null) return;

        if (typeof val !== 'undefined') this.max = parseInt(val) || 0;

        const n_decimanles = 0;
        const duracion = 2.5;
        let contador = new CountUp(this.contenedor.find('.value')[0], this.min, this.max, n_decimanles, duracion, {
            useEasing: true,
            useGrouping: true,
            separator: ',',
            decimal: '.'
        });
        contador.start();
    }


    valor(val) {
        if (this.contenedor === null) return;

        const $el = this.contenedor.find('.value');

        $el.text(val);
    }


    reiniciar() {
        this.valor(this.min);
    }


    minimo(val) {
        this.min = val;
    }


    maximo(val) {
        this.max = val;
    }


    cargarTotal() {
        if (this.fuente === null || this.url === null) return;

        const fn_contar = this.contar.bind(this);

        $.ajax({
            type: 'GET',
            url: this.url,
            data: { '_fuente': this.fuente },
            dataType: 'json'
        }).done(function(data) {
            if (data['ok'] == 1) {
                fn_contar(data['total']);
            }
        });
    }
}